import React from 'react'
import "../components/styles/TestimonialsCard.css"
import Person1 from "../components/assets/img1.png"
import Person2 from "../components/assets/img2.png"
import Person3 from "../components/assets/img3.png"

const TestimonialsCard = () => {
    const Reviews = [
        {   
            image: Person1,
            name: "Jason Cross",
            review: "WOW! This is my favorite restaurant in Ann Arbor by far"
        },
        {
            image: Person2,
            name: "Marie Kay",
            review: "WOW! This is my favorite restaurant in Ann Arbor by far"
        },
        {
            image: Person3,
            name: "Vivian Cox",
            review: "WOW! This is my favorite restaurant in Ann Arbor by far"
        }
    ]

    const Testimonials = Reviews.map(person => {
            const image = person.image;
            const name = person.name;
            const review = person.review

            return (
                <div>
                    <div className="testimonial-card-container">
                        <div className='top'>
                            <div className="image-container">
                                    <img src={image} alt="person" />
                            </div>
                            <div className='name'>
                                <h3>{name}</h3>
                            </div>
                        </div>
                        <div className="testimonial-card-text">
                            <p>{review}</p>
                        </div>
                    </div>
                </div>
            )
    })


  return (
    <div>
      <div className="special-card">
        {Testimonials}
      </div>
    </div>
  )
}

export default TestimonialsCard
